import './App.css';
import MyComponent from './components/MyComponent';

function App() {
  return (
    <div className="App">
      {/* CSS Global */}
      <h1>React com CSS</h1>

      {/* CSS de componente */}
      <MyComponent />
      <p>Este parágrafo é do App.js</p>

      {/* Inline CSS */}
      <p style={{color: "blue", padding: "25px", borderTop: "2px solid"}}>Este elemento foi estilizado de forma inline</p>
    </div>
  );
}

export default App;
